<script>
export default {
    name: 'Config_Cores',
    methods: {
        colorblind(event, daltonismo) {
            var firstrule = document.getElementById("filtro")
            switch (daltonismo) {
                case "protanopia":
                    firstrule.style.filter = "url(/filters.svg#protanopia)";
                    break;
                case "protanomalia":
                    firstrule.style.filter = "url(/filters.svg#protanomaly)";
                    break;
                case "deuteranopia":
                    firstrule.style.filter = "url(/filters.svg#deuteranopia)";
                    break;
                case "deuteranomalia":
                    firstrule.style.filter = "url(/filters.svg#deuteranomaly)";
                    break;
                case "tritanopia":
                    firstrule.style.filter = "url(/filters.svg#tritanopia)";
                    break;
                case 'tritanomalia':
                    firstrule.style.filter = "url(/filters.svg#tritanomaly)";
                    break;
                case "monocromacia":
                    firstrule.style.filter = "grayscale(1)";
                    break;
                default:
                    firstrule.style.filter = "none";
            }
        }
    },
    created() {
        document.getElementById("geral").href = "../geral.css";
        document.getElementById("meucss").href = "../bckg/configs.css";
    }
}

</script>

